# Cell
def unauth(event, *args):
  unauthCreden = cognito.unAuthCreden()
  cleanedCreden = {k:(v.timestamp() if type(v)==datetime else v) for k,v in unauthCreden.items()}
  return Response.returnSuccess(cleanedCreden)

