def Bubble(array) -> list:
    """
    Sorts a list with the bubble sorting algorithm.
    :param array: Iterable of ints or floats.
    :return: Sorted list.
    """
    array = list(array[:])
    while True:
        done = True
        for i in range(len(array)-1):
            if array[i] > array[i+1]:
                array[i], array[i+1] = array[i+1], array[i]
                done = False
        if done:
            break

    return array
